pragma solidity >=0.4.22 <0.7.0;

import "./interfaces/ConvertStrategy.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/ownership/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract ClaimConvertStrategy is ConvertStrategy, Ownable {

    using SafeMath for uint256;

    mapping(address => uint256) public tokens;
    mapping(address => string) public accounts;
    mapping(address => string) public keys;

    IERC20 public token;

    address[] public participants;

    constructor(IERC20 _token) public {
        token = _token;
    }

    // @override
    function convert(address _from, string calldata _eosOpenKey, string calldata _eosAccName, uint256 _amount) onlyOwner external {
        tokens[_from] = tokens[_from].add(_amount);
        accounts[_from] = _eosAccName;
        keys[_from] = _eosOpenKey;

        token.transferFrom(_from, owner(), _amount);

        addNewParticipant(_from);
    }

    function addNewParticipant(address _addr) private {
        uint arrayLength = participants.length;

        // if address already in array than exit function
        for (uint i = 0; i < arrayLength; i++) {
            if (participants[i] == _addr) {
                return;
            }
        }

        // else add address to participants
        participants.push(_addr);
    }
}
